mapper.select:
SELECT

FROM academic_resume


        AND `student_id`=#{studentId}
        AND `school_name`=#{schoolName}
        AND `school_id`=#{schoolId}
        AND `grade_id`=#{gradeId}
        AND `grade_name`=#{gradeName}
        AND `class_id`=#{classId}
        AND `class_name`=#{className}
        AND `semester_id`=#{semesterId}
        AND `semester_name`=#{semesterName}
        AND `student_name`=#{studentName}
        AND `student_code`=#{studentCode}
        AND `gender`=#{gender}
        AND `score_type`=#{scoreType}
        AND `creator_id`=#{creatorId}
        AND `modifier_id`=#{modifierId}
        AND `created_time`=#{createdTime}
        AND `modified_time`=#{modifiedTime}

SELECT

FROM academic_resume ar INNER JOIN archive_score archive on ar.score_id=archive.id and
ar.score_type=archive.type


        AND ar.`id`=#{id}
        AND `student_id`=#{studentId}
        AND `school_name`=#{schoolName}
        AND ar.`school_id`=#{schoolId}
        AND ar.`grade_id`=#{gradeId}
        AND ar.`grade_name`=#{gradeName}
        AND ar.`class_id`=#{classId}
        AND ar.`class_name`=#{className}
        AND ar.`semester_id`=#{semesterId}
        AND ar.`semester_name`=#{semesterName}
        AND ar.`student_name`=#{studentName}
        AND ar.`student_code`=#{studentCode}
        AND ar.`gender`=#{gender}
        AND ar.`score_type`=#{scoreType}
        AND ar.`creator_id`=#{creatorId}
        AND ar.`modifier_id`=#{modifierId}
        AND ar.`created_time`=#{createdTime}
        AND ar.`modified_time`=#{modifiedTime}

SELECT

