mapper.select:
SELECT   FROM academic_resume


        AND `school`=#{school}
        AND `school_id`=#{schoolId}
        AND `grade_id`=#{gradeId}
        AND `grade_name`=#{gradeName}
        AND `class_id`=#{classId}
        AND `class_name`=#{className}
        AND `semester_code`=#{semesterCode}
        AND `semester_name`=#{semesterName}
        AND `student_name`=#{studentName}
        AND `student_code`=#{studentCode}
        AND `gender`=#{gender}
        AND `score_id`=#{scoreId}
        AND `score_type`=#{scoreType}
        AND `created_founder_id`=#{createdFounderId}
        AND `updated_founder_id`=#{updatedFounderId}
        AND `created_time`=#{createdTime}
        AND `updated_time`=#{updatedTime}

SELECT  FROM academic_resume ar INNER JOIN archive archive on ar.score_id=archive.id and ar.score_type=archive.type


        AND ar.`id`=#{id}
        AND ar.`school`=#{school}
        AND ar.`school_id`=#{schoolId}
        AND ar.`grade_id`=#{gradeId}
        AND ar.`grade_name`=#{gradeName}
        AND ar.`class_id`=#{classId}
        AND ar.`class_name`=#{className}
        AND ar.`semester_code`=#{semesterCode}
        AND ar.`semester_name`=#{semesterName}
        AND ar.`student_name`=#{studentName}
        AND ar.`student_code`=#{studentCode}
        AND ar.`gender`=#{gender}
        AND ar.`score_id`=#{scoreId}
        AND ar.`score_type`=#{scoreType}
        AND ar.`created_founder_id`=#{createdFounderId}
        AND ar.`updated_founder_id`=#{updatedFounderId}
        AND ar.`created_time`=#{createdTime}
        AND ar.`updated_time`=#{updatedTime}

SELECT  FROM academic_resume ar INNER JOIN logic_score score on ar.score_id=score.id and ar.score_type=score.type


        AND ar.`id`=#{id}
        AND ar.`school`=#{school}
        AND ar.`school_id`=#{schoolId}
        AND ar.`grade_id`=#{gradeId}
        AND ar.`grade_name`=#{gradeName}
